    public void onSubscribe(Disposable d) {
        print("onSubscribe: " + Thread.currentThread().getName());
    }

    @Override
    public void onSuccess(String s) {
        print("onSuccess: " + s);
        print("onSuccess: " + Thread.currentThread().getName());
    }
      // ...
});

