var mysql = require('mysql');

var sqlOptions = {
    host: '127.0.0.1',
    user: 'root',
    password: '111111',
    database: 'mydb',
    port: 3306
};

var pool = mysql.createPool(sqlOptions);

function query(sql, callback) {
    pool.getConnection(function (err, conn) {
        if (err) {
            callback(err, null);
        } else {
            conn.query(sql, function (err, result) {
                callback(err, result);
                conn.release();
            });
        }
    });
}

